#include "stdlib/stats/base/dists/negative_binomial/logpmf.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/assert/is_nonnegative_integer.h"
#include "stdlib/math/base/special/ln.h"
#include "stdlib/constants/float64/ninf.h"
#include "stdlib/math/base/dists/ibeta_derivative.h"

/**
* Evaluates the natural logarithm of the probability mass function (PMF) for a negative binomial distribution with number of successes until experiment is stopped `r` and success probability `p`.
*
* @param x    input value
* @param r    number of successes until experiment is stopped
* @param p    success probability
* @return     evaluated logPMF
*
* @example
* double y = stdlib_base_dists_negative_binomial_logpmf( 5.0, 20.0, 0.8 );
* // returns ~-1.853
*/
double stdlib_base_dists_negative_binomial_logpmf( const double x, const double r, const double p ) {
	if (
		stdlib_base_is_nan( x ) ||
		stdlib_base_is_nan( r ) ||
		stdlib_base_is_nan( p ) ||
		r <= 0.0 ||
		p <= 0.0 ||
		p > 1.0
	) {
		return stdlib_base_nan(); // NaN
	}
	if ( !stdlib_base_is_nonnegative_integer( x ) || p == 0.0 ) {
		return STDLIB_CONSTANT_FLOAT64_NINF;
	}
	return stdlib_base_ln( p ) - stdlib_base_ln( r + x ) + stdlib_base_ln( stdlib_base_dists_ibeta_derivative( p, r, x + 1.0 ) );
}
