'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var tryRequire = require( '@stdlib/utils/try-require' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var EPS = require( '@stdlib/constants/float64/eps' );
var abs = require( '@stdlib/math/base/special/abs' );


// FIXTURES //

var highHigh = require( './fixtures/julia/high_high.json' );
var highSmall = require( './fixtures/julia/high_small.json' );
var smallHigh = require( './fixtures/julia/small_high.json' );
var smallSmall = require( './fixtures/julia/small_small.json' );


// VARIABLES //

var logpmf = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( logpmf instanceof Error )
};


// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof logpmf, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', opts, function test( t ) {
	var y = logpmf( NaN, 20.0, 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = logpmf( 5.0, NaN, 0.5 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = logpmf( 5.0, 20.0, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided invalid `r`, `p`, or `x`, the function returns `NaN`', opts, function test( t ) {
	var y;

	y = logpmf( 2.0, 0.0, 0.5 );
	t.equal( isnan( y ), true, 'returns NaN when r is zero' );

	y = logpmf( 2.0, -2.0, 0.5 );
	t.equal( isnan( y ), true, 'returns NaN when r is negative' );

	y = logpmf( 2.0, 20, -1.0 );
	t.equal( isnan( y ), true, 'returns NaN when p is negative' );

	y = logpmf( 2.0, 20, 1.5 );
	t.equal( isnan( y ), true, 'returns NaN when p is greater than 1' );

	t.end();
});

tape( 'if provided `x` not a non-negative integer, the function returns `-Infinity`', opts, function test( t ) {
	var y = logpmf( 2.5, 20.0, 0.5 );
	t.equal( y, NINF, 'returns -Infinity when x is not an integer' );
	t.end();
});

tape( 'evaluates logPMF for high-high range', opts, function test( t ) {
	var expected;
	var delta;
	var tol;
	var x;
	var r;
	var p;
	var y;
	var i;

	expected = highHigh.expected;
	x = highHigh.x;
	r = highHigh.r;
	p = highHigh.p;
	for ( i = 0; i < x.length; i++ ) {
		y = logpmf( x[i], r[i], p[i] );
		if ( y === expected[i] ) {
			t.equal( y, expected[i], 'x: '+x[i]+', r: '+r[i]+', p: '+p[i]+', y: '+y+', expected: '+expected[i] );
		} else {
			delta = abs( y - expected[ i ] );
			tol = 2.0 * EPS * abs( expected[ i ] );
			t.ok( delta <= tol, 'within tolerance. x: '+x[ i ]+'. r: '+r[i]+'. p: '+p[i]+'. y: '+y+'. E: '+expected[ i ]+'. Δ: '+delta+'. tol: '+tol+'.' );
		}
	}
	t.end();
});

