'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var Float64Array = require( '@stdlib/array/float64' );
var uniform = require( '@stdlib/random/base/uniform' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var pkg = require( './../package.json' ).name;
var pdf = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var len;
	var x;
	var a;
	var bmin;
	var bmax;
	var mu;
	var sigma;
	var y;
	var i;

	len = 100;
	x = new Float64Array( len );
	a = new Float64Array( len );
	bmin = new Float64Array( len );
	bmax = new Float64Array( len );
	mu = new Float64Array( len );
	sigma = new Float64Array( len );

	for ( i = 0; i < len; i++ ) {
		x[ i ] = uniform( -10.0, 10.0 );
		a[ i ] = uniform( -20.0, 0.0 );
		bmin[ i ] = uniform( a[ i ], a[ i ] + 40.0 );
		bmax[ i ] = uniform( 0.1, 5.0 );
		mu[ i ] = uniform( -5.0, 5.0 );
		sigma[ i ] = uniform( 0.1, 5.0 );
	}

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		y = pdf( x[ i % len ], a[ i % len ], bmin[ i % len ], mu[ i % len ], sigma[ i % len ] );
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
	}
	b.toc();
	if ( isnan( y ) ) {
		b.fail( 'should not return NaN' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+':factory', function benchmark( b ) {
	var mypdf;
	var sigma;
	var bmin;
	var len;
	var mu;
	var a;
	var x;
	var y;
	var i;

	sigma = 1.0;
	bmin = 1.0;
	len = 100;
	mu = 0.5;
	a = 0.0;
	x = new Float64Array( len );
	mypdf = pdf.factory( a, bmin, mu, sigma );
	for ( i = 0; i < len; i++ ) {
		x[ i ] = uniform( -2.0, 2.0 );
	}

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		y = mypdf( x[ i % len ] );
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
	}
	b.toc();
	if ( isnan( y ) ) {
		b.fail( 'should not return NaN' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
