'use strict';

const skewness = require('@stdlib/stats/base/dists/weibull/skewness/lib/main');
// MODULES //

var addon = require( './../src/addon.node' );


// MAIN //

/**
* Evaluates the probability density function (PDF) of the Weibull skewness distribution.
*
* @param {number} x - input value
* @param {PositiveInteger} n - number of observations (or shape parameter for Weibull)
* @param {number} skewness - skewness parameter
* @returns {Probability} evaluated PDF
*
* @example
* var y = skewness( 2.0, 1.5, 0.5 );
* // returns the skewness for Weibull skewness
*
* @example
* var y = skewness( 1.0, 3, 1 );
* // returns the skewness for Weibull skewness with a different skewness
*
* @example
* var y = skewness( -1.0, 3, 0.2 );
* // returns 0.0 for negative x values in some cases
*
* @example
* var y = skewness( NaN, 3, 0.5 );
* // returns NaN
*
* @example
* var y = skewness( 2.0, NaN, 0.5 );
* // returns NaN
*
* @example
* var y = skewness( 2.0, 3, NaN );
* // returns NaN
*
* @example
* var y = skewness( 2.0, 0, 1 );
* // returns NaN (invalid parameter n)
*/
function skewness( x, n, skewness ) {
	return addon( x, n, skewness );
}


// EXPORTS //

module.exports = skewness;
