'use strict';

// MODULES //

var addon = require( './../src/addon.node' );


// MAIN //

/**
* Evaluates the probability density function (PDF) of the Weibull skewness distribution.
*
* @param {number} k - shape parameter
* @param {PositiveInteger} lambda - scale parameter
* @returns {number} evaluated skewness
*
* @example
* var y = skewness( 2.0, 1.5 );
* // returns the skewness for Weibull skewness
*
* @example
* var y = skewness( 1.0, 3 );
* // returns the skewness for Weibull skewness with a different skewness
*
* @example
* var y = skewness( -1.0, 3 );
* // returns 0.0 for negative x values in some cases
*
* @example
* var y = skewness( NaN, 3 );
* // returns NaN
*
* @example
* var y = skewness( 2.0, NaN );
* // returns NaN
*
* @example
* var y = skewness( 2.0, 3 );
* // returns NaN
*
* @example
* var y = skewness( 2.0, 0 );
* // returns NaN (invalid parameter n)
*/
function skewness( k, lambda ) {
	return addon( k, lambda );
}


// EXPORTS //

module.exports = skewness;
