'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var tryRequire = require( '@stdlib/utils/try-require' );
var abs = require( '@stdlib/math/base/special/abs' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var EPS = require( '@stdlib/constants/float64/eps' );

// FIXTURES //
var data = require( './fixtures/julia/data.json' );

// VARIABLES //
var skewness = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( skewness instanceof Error )
};

// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof skewness, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', opts, function test( t ) {
	var y = skewness( NaN, 8 );
	t.equal( isnan( y ), true, 'returns NaN' );
	y = skewness( 0.0, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided `+infinity` for `x` and a valid `n`, the function returns `NaN`', opts, function test( t ) {
	var y = skewness( PINF, 8 );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided `-infinity` for `x` and a valid `n`, the function returns `NaN`', opts, function test( t ) {
	var y = skewness( NINF, 8 );
	t.equal( isnan( y ), true, 'returns NaN' );
	t.end();
});

tape( 'if not provided a positive integer for `n`, the function returns `NaN`', opts, function test( t ) {
	var y;

	y = skewness( 2.0, -1.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = skewness( 2.0, NINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = skewness( 2.0, PINF );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'the function evaluates the skewness for `k` given `lambda` observations', opts, function test( t ) {
	var expected;
	var delta;
    var lambda;
	var tol;
	var i;
	var k;
	var y;

	expected = data.expected;
	k = data.k; 
	lambda = data.lambda;
	for ( i = 0; i < k.length; i++ ) {
		y = skewness( k[i], lambda[i] );
		if ( y === expected[i] ) {
			t.equal( y, expected[i], 'k: '+k[i]+', lambda: '+lambda[i]+', y: '+y+', expected: '+expected[i] );
		} else {
			delta = abs( y - expected[ i ] );
            tol = 1e-11 * abs( expected[ i ] );
			t.ok( delta <= tol, 'within tolerance. k: '+k[ i ]+'. lambda: '+lambda[i]+'. y: '+y+'. E: '+expected[ i ]+'. Î”: '+delta+'. tol: '+tol+'.' );
		}
	}
	t.end();
});

